import React from "react";

class Chatbot extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      field: null, // Stores the selected field
      response: null, // Stores the server response
    };
  }

  componentDidMount() {
    // Fetch the selected field and response from the parent component (props)
    const { selectedField } = this.props;

    if (selectedField) {
      // Make a request to the backend with the selected field
      fetch("http://localhost:5000/process-field", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ field: selectedField }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Failed to fetch field data");
          }
          return response.json();
        })
        .then((data) => {
          this.setState({ field: data.field, response: data.message });
        })
        .catch((error) => {
          console.error("Error fetching field response:", error);
          this.setState({ response: "Something went wrong. Please try again." });
        });
    }
  }

  render() {
    const { field, response } = this.state;

    return (
      <main>
        <nav className="outer-col-1">Navigation</nav>
        <div className="outer-col-2">
          <header>Header</header>
          <div className="inner-row">
            <div className="inner-col">
              <article>
                <h2>Selected Field: {field || "None"}</h2>
                <p>Response: {response || "Loading..."}</p>
              </article>
              <footer>Footer</footer>
            </div>
            <aside>Aside</aside>
          </div>
        </div>
      </main>
    );
  }
}

export default Chatbot;
